(async (
    entityValue, //  the current entity to compute
    currentValue, // the current value of the field
    api // the API object to use for do api calls
  )=> { 
    try {
  const customerId = entityValue["packageCustomerId"];
  const exportId = entityValue["exportId"];

  if (!customerId) {
    console.log("Customer not found for id: ", customerId);
    return null;
  }
  let res = await api.get(`generic-entities/${customerId}`);
  if (!res.data) {
    console.log("Customer not found for id: ", customerId);
    return null;
  }
  const customer = res.data;
  console.log("customer: ", customer);
  let packageNumber = 0;
  res = await api.get(`generic-entities`, {
    params: {
      searchFields: {
        ["value.packageCustomerId"]: customerId,
        "value.exportId": exportId ? exportId : "",
        name: "package",
      },
    },
  });
  if (!res) {
    return null;
  }

  for (const d of res?.data?.data || []) {
    if (d.value["packageOrderNumber"] > packageNumber) {
      packageNumber = d.value["packageOrderNumber"];
    }
  }

  return `${customer?.value["customerName"]}_${packageNumber + 1}`;
    } catch(e) {
      console.log(e)
    }
  })(entityValue, currentValue, api);

(async (
    entityValue, //  the current entity to compute
    currentValue, // the current value of the field
    api // the API object to use for do api calls
  )=> { 

    const exportId = entityValue['exportId'];
    const customerId = entityValue['packageCustomerId'];
    console.log("exportId: ", exportId, "customerId: ", customerId);

    if (!exportId) {
      return 1;
    }
    let res;

    res = await api.get("generic-entities", {
      params: {
        searchFields: {
          [`value.exportId`]: exportId,
          [`value.packageCustomerId`]: customerId,
          name: "package",
        },
      },
    });
    const packages = res?.data;
    if (!packages) {
      console.log("can't find packages for exportId: ", exportId);
      return null;
    }

    let maxValue = 0;
    for (const _package of packages.data) {
      if (_package.value["packageOrderNumber"] > maxValue) {
        maxValue = _package.value["packageOrderNumber"];
      }
    }

    return maxValue + 1;

  })(entityValue, currentValue, api);

(async (
    entityValue, //  the current value of the entity to compute
    currentValue, // the current value of the field
    api // the API object to do api calls
  )=> { 

    const productsPacked =
      entityValue['productsPacked'];
    console.log("productsPacked: ", productsPacked);
    if (!productsPacked) {
      return null;
    }
    let weight = 0;
    for (let i = 0; i < productsPacked.length; i++) {

      const weightValue = parseFloat(
        entityValue['productsPacked'][i]['productPackageWeight']
      );
      if (!isNaN(weightValue)) {
        weight += weightValue;
      }
    }

    return weight;
  })(entityValue, currentValue, api);

