(async (
    entityValue, //  the current value of the entity to compute
    currentValue, // the current value of the field
    api // the API object to do api calls
  )=> { 
  const quantity = parseFloat(entityValue.quantity);
  const productId = entityValue.productId;

  if (!productId || isNaN(quantity)) {
    return 0;
  }
  const res = await api?.get(`generic-entities/${productId}`);
  const product = res?.data;
  let weight = parseFloat(product.value?.weight);
  if (isNaN(weight)) {
    return 0;
  }

  return weight * quantity;
  })(entityValue, currentValue, api);

